from bs4 import BeautifulSoup
import re

def html_to_text(body_content):
    """Main function to clean HTML email content"""
    try:
        if not body_content.strip():
            return ""
            
        soup = BeautifulSoup(body_content, "html.parser")
        
        for element in soup(["script", "style", "head", "meta", "link"]):
            element.decompose()
        
        # Get text with preserved line breaks
        text = soup.get_text(separator='\n')
        
        # Enhanced processing pipeline
        text = remove_signatures_and_disclaimers(text)
        text = remove_quoted_threads(text)
        text = remove_unsubscribe_sections(text)
        text = remove_inline_quote_symbols(text) # New function added
        text = normalize_whitespace(text)
        
        return text.strip()
    
    except Exception as e:
        print(f"Error processing email: {str(e)}")
        return body_content

def remove_quoted_threads(text):
    """Removes standard quoted reply blocks."""
    quote_pattern = re.compile(
        r'^\s*>[>]*.*|^On\s.*wrote:.*|^-+original\s+message-+.*',
        flags=re.IGNORECASE | re.MULTILINE | re.DOTALL
    )
    
    lines = text.splitlines()
    cleaned_lines = []
    
    for line in lines:
        if not quote_pattern.match(line):
            cleaned_lines.append(line)
        else:

            break
            
    return "\n".join(cleaned_lines)

