def remove_signatures_and_disclaimers(text):
    """Improved version that reliably removes all signature content"""
    # Patterns for signature blocks and disclaimers
    patterns = [
        # Signature starters
        r'(Regards|Sincerely|Best|Cheers|Kind regards)[\s\S]*?$',
        # Contact info patterns
        r'M:\s*\d{10}.*?$',
        r'E:\s*\S+@\S+.*?$',
        r'Skype:\s*\S+.*?$',
        # Company info
        r'\*BrightPath Technology.*?$',
        r'#\d+.*?Bangalore - 560068.*?$',
        # Disclaimer blocks
        r'DISCLAIMER[\s\S]*?(viruses|unlawful)\.?',
        r'confidential and may be legally privileged[\s\S]*?viruses\.?'
    ]
    
    for pattern in patterns:
        text = re.sub(pattern, '', text, flags=re.IGNORECASE|re.MULTILINE)
    
    return text


def remove_unsubscribe_sections(text):
    """Remove unsubscribe and privacy policy sections."""
    return re.sub(
        r'(unsubscribe|privacy policy|terms of service).*?$',
        '',
        text,
        flags=re.IGNORECASE | re.MULTILINE
    )

def remove_inline_quote_symbols(text):
    """Removes any instance of > or >> from within the text."""
    return re.sub(r'>>+', '', text)

def normalize_whitespace(text):
    """Clean up whitespace while preserving paragraph breaks."""
    text = re.sub(r'[ \t]+', ' ', text)
    text = re.sub(r'\n{3,}', '\n\n', text)
    return text.strip()
    
